    				tri.T1 = tri.V1
    				tri.T2 = tri.V2
    				tri.T3 = tri.V3
    				triangles.Add(tri)
    				tri = New RichTriangle(i + j * cols, i + (j + 1) * cols + 1, i + (j + 1) * cols)
    				tri.T1 = tri.V1
    				tri.T2 = tri.V2
    				tri.T3 = tri.V3
    				triangles.Add(tri)
    			Next
