   Dim id As Integer = ViewportProfessional1.GetEntityUnderMouseCursor( _ 
         e.Location)

   If id <> -1 Then

      Dim ent As Entity = ViewportProfessional1.Entities(id)

      ent.Selected = True

   End If

   ViewportProfessional1.Invalidate()

End Sub

